    max-height: 200px;
}

.faq-answer p {
    color: #666;
    line-height: 1.6;
    margin: 0;
}

/* Refer Section */
.refer {
    padding: 80px 0;
    background: linear-gradient(135deg, #fce4ec, #f8bbd9);
}

.refer-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
    align-items: center;
}

.refer-text h2 {
    color: #2e2e2e;
    font-size: 2.5rem;
    margin-bottom: 0.5rem;
    font-weight: 700;
}

.refer-text h3 {
    color: #e91e63;
    font-size: 1.5rem;
    margin-bottom: 1rem;
    font-weight: 600;
}

.refer-text p {
    color: #666;
    font-size: 1.1rem;
    line-height: 1.6;
    margin-bottom: 2rem;
}

.refer-steps {
    display: flex;
    gap: 1rem;
    margin-bottom: 2rem;
}

.step {
    text-align: center;
    flex: 1;
}

.step-number {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: #e91e63;
    color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: bold;
    margin: 0 auto 0.5rem;
}

.step p {
    color: #2e2e2e;
    font-weight: 500;
    font-size: 0.9rem;
    margin: 0;
}

.referral-code {
    background: white;
    padding: 1.5rem;
    border-radius: 10px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

.referral-code p {
    margin-bottom: 1rem;
    color: #2e2e2e;
}

.share-btn {
    background: linear-gradient(135deg, #25d366, #128c7e);
    color: white;
    border: none;
    padding: 0.75rem 1.5rem;
    border-radius: 8px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
}

.share-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(37, 211, 102, 0.3);
}

.refer-image img {
    width: 100%;
    height: auto;
    border-radius: 15px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

/* Contact Section */
.contact {
    padding: 80px 0;
    background: white;
}

.contact-container {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
}

.contact-info h3 {
    color: #2e2e2e;
    font-size: 1.8rem;
    margin-bottom: 2rem;
    font-weight: 600;
}

.contact-item {
    display: flex;
    align-items: flex-start;
    gap: 1rem;
    margin-bottom: 2rem;
}

.contact-item i {
    font-size: 1.5rem;
    color: #e91e63;
    margin-top: 0.25rem;
    min-width: 24px;
}

.contact-item h4 {
    color: #2e2e2e;
    margin-bottom: 0.25rem;
    font-weight: 600;
}

.contact-item p {
    color: #666;
    margin: 0;
    line-height: 1.5;
}

.contact-form {
    background: #fafafa;
    padding: 2rem;
    border-radius: 15px;
}

.contact-form h3 {
    color: #2e2e2e;
    margin-bottom: 1.5rem;
    font-weight: 600;
}

.submit-btn {
    width: 100%;
    background: linear-gradient(135deg, #e91e63, #ad1457);
    color: white;
    border: none;
    padding: 1rem;
    border-radius: 8px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
}

.submit-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(233, 30, 99, 0.3);
}

/* Modal Styles */
.modal {
    display: none;
    position: fixed;
    z-index: 10000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    backdrop-filter: blur(5px);
}

.modal-content {
    background-color: white;
    margin: 5% auto;
    padding: 2rem;
    border-radius: 15px;
    width: 90%;
    max-width: 400px;
    position: relative;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
}

.close {
    position: absolute;
    right: 1rem;
    top: 1rem;
    font-size: 2rem;
    font-weight: bold;
    cursor: pointer;
    color: #999;
    transition: color 0.3s ease;
}

.close:hover {
    color: #e91e63;
}

.auth-tabs {
    display: flex;
    border-bottom: 1px solid #e0e0e0;
    margin-bottom: 2rem;
}

.tab-btn {
    flex: 1;
    padding: 1rem;
    border: none;
    background: none;
    cursor: pointer;
    font-weight: 600;
    color: #666;
    transition: all 0.3s ease;
}

.tab-btn.active {
    color: #e91e63;
    border-bottom: 2px solid #e91e63;
}

.tab-content {
    display: none;
}

.tab-content.active {
    display: block;
}

.tab-content h3 {
    color: #2e2e2e;
    margin-bottom: 1.5rem;
    text-align: center;
}

.auth-btn {
    width: 100%;
    background: linear-gradient(135deg, #e91e63, #ad1457);
    color: white;
    border: none;
    padding: 1rem;
    border-radius: 8px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    margin-bottom: 1rem;
}

.auth-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(233, 30, 99, 0.3);
}

.tab-content p {
    text-align: center;
    color: #666;
    margin: 0;
}

.tab-content a {
    color: #e91e63;
    text-decoration: none;
    font-weight: 600;
}

/* Service Detail Pages */
.service-detail {
    padding: 80px 0;
    background: white;
    display: none;
}

.service-detail.active {
    display: block;
}

.service-header {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
    align-items: center;
    margin-bottom: 3rem;
}

.service-header img {
    width: 100%;
    height: 300px;
    object-fit: cover;
    border-radius: 15px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.service-info h2 {
    font-size: 2.5rem;
    color: #2e2e2e;
    margin-bottom: 1rem;
    font-weight: 700;
}

.service-info p {
    font-size: 1.1rem;
    color: #666;
    line-height: 1.6;
    margin-top: 1rem;
}

.service-details-grid {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 3rem;
}

.service-options h3 {
    font-size: 1.5rem;
    color: #2e2e2e;
    margin-bottom: 1.5rem;
    font-weight: 600;
}

.option-card {
    background: #fafafa;
    padding: 1.5rem;
    border-radius: 10px;
    margin-bottom: 1rem;
    border-left: 4px solid #e91e63;
}

.option-card h4 {
    color: #2e2e2e;
    font-size: 1.2rem;
    margin-bottom: 0.5rem;
    font-weight: 600;
}

.option-card p {
    color: #666;
    margin-bottom: 0.75rem;
    font-size: 0.9rem;
}

.option-card .price {
    color: #e91e63;
    font-weight: 700;
    font-size: 1.1rem;
}

.booking-form {
    background: linear-gradient(135deg, #fce4ec, #f8bbd9);
    padding: 2rem;
    border-radius: 15px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.booking-form h3 {
    color: #2e2e2e;
    margin-bottom: 1.5rem;
    font-weight: 600;
    text-align: center;
}

.book-btn {
    width: 100%;
    background: linear-gradient(135deg, #25d366, #128c7e);
    color: white;
    border: none;
    padding: 1rem;
    border-radius: 10px;
    font-weight: 600;
    font-size: 1.1rem;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
}

.book-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(37, 211, 102, 0.3);
}

.book-btn::before {
    content: 'ðŸ“±';
    font-size: 1.2rem;
}

/* Navigation Enhancement */
.nav-link {
    position: relative;
    transition: all 0.3s ease;
}

.nav-link:hover {
    color: #e91e63;
    transform: translateY(-1px);
}

.nav-link.active {
    color: #e91e63;
    font-weight: 600;
}

.nav-link.active::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 100%;
    height: 2px;
    background: #e91e63;
    border-radius: 2px;
}

/* Responsive Updates */
@media (max-width: 768px) {
    .nav {
        flex-wrap: wrap;
        gap: 1rem;
    }

    .nav-dropdown {
        order: -1;
        width: 100%;
    }

    .dropdown-menu {
        position: static;
        opacity: 1;
        visibility: visible;
        transform: none;
        box-shadow: none;
        background: none;
        padding: 0;
        display: flex;
        gap: 1rem;
        flex-wrap: wrap;
    }

    .about-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .features-grid {
        grid-template-columns: 1fr;
    }

    .about-stats {
        flex-direction: row;
        gap: 1rem;
    }

    .stat-item {
        padding: 1rem;
    }

    .stat-item h3 {
        font-size: 1.8rem;
    }

    .reviews-grid {
        grid-template-columns: 1fr;
    }

    .products-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .refer-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .refer-steps {
        flex-direction: column;
        gap: 1rem;
    }

    .step {
        display: flex;
        align-items: center;
        gap: 1rem;
        text-align: left;
    }

    .step-number {
        margin: 0;
    }

    .contact-container {
        grid-template-columns: 1fr;
        gap: 2rem;
    }
}

@media (max-width: 480px) {
    .products-grid {
        grid-template-columns: 1fr;
    }

    .about-stats {
        flex-direction: column;
        gap: 1rem;
    }

    .refer-text h2 {
        font-size: 2rem;
    }

    .refer-text h3 {
        font-size: 1.2rem;
    }

    .service-header {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .service-details-grid {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .service-info h2 {
        font-size: 2rem;
    }
}

/* Help Text Styling */
.help-text {
    display: block;
    font-size: 0.8rem;
    color: #666;
    margin-top: 0.25rem;
    font-style: italic;
}

.form-group input[type="url"] {
    background: #f8f9fa;
    border: 1px solid #ddd;
    transition: all 0.3s ease;
}

.form-group input[type="url"]:focus {
    border-color: #e91e63;
    background: white;
    box-shadow: 0 0 0 3px rgba(233, 30, 99, 0.1);
}

/* Location Input Container */
.location-inputs {
    display: flex;
    gap: 0.5rem;
    align-items: center;
}

.location-inputs input {
    flex: 1;
}

.location-btn {
    background: linear-gradient(135deg, #4caf50, #45a049);
    color: white;
    border: none;
    padding: 0.75rem 1rem;
    border-radius: 8px;
    font-weight: 600;
    font-size: 0.9rem;
    cursor: pointer;
    transition: all 0.3s ease;
    white-space: nowrap;
    min-width: 140px;
}

.location-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(76, 175, 80, 0.3);
}

.location-btn:disabled {
    opacity: 0.7;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

.location-btn:active {
    transform: translateY(0);
}

/* Responsive location inputs */
@media (max-width: 768px) {
    .location-inputs {
        flex-direction: column;
        gap: 0.75rem;
    }

    .location-inputs input {
        width: 100%;
    }

    .location-btn {
        width: 100%;
        min-width: unset;
    }
}
